const ContactForm = ({ handleContactModal }: Props): JSX.Element => {
	const dispatch = useDispatch();
	const theme = useTheme();

	const { values, errors, hasError, handleFormChange, handleSubmit } =
		useFormState({
			onSubmit: async ({ email, password }) => {},
			formErrors: (formValues) => validate(formValues, schema),
		});

	return (
		<Box>
			<form name="email-login" onSubmit={handleSubmit}>
				<Grid container spacing={4}>
					<Grid item xs={12}>
						<TextField
							placeholder="Email"
							label="Email *"
							variant="outlined"
							size="medium"
							name="email"
							fullWidth
							helperText={hasError('email') ? errors.email[0] : null}
							error={hasError('email')}
							onChange={handleFormChange}
							type="email"
							value={values.email || ''}
						/>
					</Grid>

					<Grid item xs={12}>
						<TextField
							label="Message"
							multiline
							rows={6}
							variant="outlined"
							color="primary"
							size="medium"
							name="message"
							fullWidth
							value={values.message}
							onChange={handleFormChange}
							error={hasError('message')}
							helperText={hasError('message') ? errors.message[0] : null}
						/>
					</Grid>

					<Grid item container xs={12}>
						<Box
							display="flex"
							flexDirection={{ xs: 'column', sm: 'row' }}
							alignItems={{ xs: 'stretched', sm: 'center' }}
							justifyContent={'space-between'}
							width={1}
							maxWidth={600}
							margin={'0 auto'}
						>
							<LoadingButton
								autoFocus
								fullWidth
								variant="contained"
								type="submit"
								color="primary"
								size="large"
								// disabled={!isValid}
								loading={false}
								loadingIndicator="Submitting..."
							>
								Submit
							</LoadingButton>
						</Box>
					</Grid>

					<Grid item xs={12}>
						<Box>
							<Typography component="p" variant="body2" align="left">
								By clicking on "submit" you agree to our{' '}
								<Box
									component="a"
									href=""
									color={theme.palette.text.primary}
									fontWeight={'700'}
								>
									Privacy Policy
								</Box>
								,{' '}
								<Box
									component="a"
									href=""
									color={theme.palette.text.primary}
									fontWeight={'700'}
								>
									Data Policy
								</Box>{' '}
								and{' '}
								<Box
									component="a"
									href=""
									color={theme.palette.text.primary}
									fontWeight={'700'}
								>
									Cookie Policy
								</Box>
								.
							</Typography>
						</Box>
					</Grid>

					<Grid item xs={12}>
						<Divider />
					</Grid>

					<Grid item xs={12}>
						<Box
							display={'flex'}
							flexDirection={'row'}
							justifyContent={'space-around'}
							marginBottom={1}
						>
							{mock.map((item) => (
								<Box
									key={fancyId()}
									component={ListItem}
									disableGutters
									width={'auto'}
									padding={0}
								>
									<Box
										component={ListItemAvatar}
										minWidth={'auto !important'}
										marginRight={2}
									>
										<Box
											component={Avatar}
											sx={{ backgroundColor: 'inherit' }}
											width={46}
											height={46}
										>
											{item.icon}
										</Box>
									</Box>
								</Box>
							))}
						</Box>
					</Grid>
				</Grid>
			</form>
		</Box>
	);
};

export default ContactForm;
